import * as types from "./ActionTypes";
import{auth} from "../firebase"
import Register from "./pages/Register";

const REGISTER_START= () =>({
    type:types.REGISTER_START,
});

const REGISTER_SUCSESS= (user) =>({
    type:types.REGISTER_SUCSESS,
    payload: user,
});

const REGISTER_FAIL= (error) =>({
    type:types.REGISTER_FAIL,
    payload: error,
});

export const registerInitiate =(email,password,DisplayName) => {
    return function(dispatch){
        dispatch(REGISTER_START());
        auth.createUserWithEmailAndPasssword(email,password).then (({user}) =>{
            user.updateProfile({
                DisplayName,
            });
    dispatch(REGISTER_SUCSESS (user));
        })
        .catch((error)=> dispatch(REGISTER_FAIL(error.massage)))
    }
}
